import { Injectable } from "@angular/core";
import { Recipe } from "../models/recipe.model";
import { AuthService } from "./auth.service";
import { HttpClient, HttpClientModule } from "@angular/common/http";
import { Observable, Subject, switchMap, map, throwError } from "rxjs";
import { catchError } from "rxjs/operators";

@Injectable({
  providedIn: "root",
})
export class RecipeService {
  recipes$ = new Subject<Recipe[]>();

  constructor(private http: HttpClient, private auth: AuthService) {}

  getAllRecipes(): Observable<Recipe[]> {
    return this.http.get<Recipe[]>("http://localhost:5400/api/recipes");
  }

  getRecipeById(id: number): Observable<Recipe> {
    return this.http.get<Recipe>(`http://localhost:5400/api/recipes/${id}`);
  }

  modifyRecipe(recipeId: number, recipe: Recipe) {
    return this.http
      .put<{ message: string }>(
        `http://localhost:5400/api/recipes/modify_recipe/${recipeId}`,
        recipe
      )
      .pipe(catchError((error) => throwError(error.error.message)));
  }

  addRecipe(formValue: {
    id: number;
    title: string;
    dietBookIds: [string];
    ingredient: [string];
    recipe: [string];
    preparationTime: number;
    breakTime: number;
    cookingTime: number;
    online: boolean;
    customerId: [string];
  }): Observable<Recipe> {
    return this.getAllRecipes().pipe(
      map((recipes) => ({
        ...formValue,
      })),
      switchMap((newRecipe) =>
        this.http.post<Recipe>(
          "http://localhost:5400/api/recipes/add_recipe",
          newRecipe
        )
      )
    );
  }
}
