import { Component, OnInit } from "@angular/core";
import {
  FormBuilder,
  FormGroup,
  FormControl,
  Validators,
} from "@angular/forms";
import { ActivatedRoute, Router } from "@angular/router";
import { Observable, EMPTY, catchError } from "rxjs";
import { tap, map, switchMap } from "rxjs/operators";
import { Recipe } from "src/app/models/recipe.model";
import { DietBooksService } from "src/app/services/diet-book.service";
import { RecipeService } from "src/app/services/recipe.service";
import { SharedService } from "src/app/services/shared.service";

@Component({
  selector: "app-recipe-form",
  templateUrl: "./recipe-form.component.html",
  styleUrls: ["./recipe-form.component.scss"],
})
export class RecipeFormComponent implements OnInit {
  recipeForm!: FormGroup;
  errorMsg!: string;
  mode!: string;
  recipeId!: number;
  loading!: boolean;
  recipeBook!: Recipe;
  recipeBook$!: Observable<Recipe>;

  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private Recipes: RecipeService,
    private route: ActivatedRoute,
    private dietBooks: DietBooksService,
    private sharedService: SharedService
  ) {}
  ngOnInit() {
    this.loading = true;
    this.recipeId = this.route.snapshot.queryParams["recipeId"];
    this.sharedService.setTextHeader(`Recette N° ${this.recipeId}`);
    this.route.queryParams
      .pipe(
        switchMap((params) => {
          if (!this.recipeId) {
            this.mode = "new";
            this.loading = false;
            this.initEmptyForm();
            return EMPTY;
          } else {
            this.mode = "edit";
            return this.Recipes.getRecipeById(params["recipeId"]);
          }
        }),
        tap((recipe) => {
          if (recipe) {
            this.recipeBook = recipe;
            this.loading = false;
            this.initModifyForm(recipe);
          }
        }),
        catchError((error) => (this.errorMsg = JSON.stringify(error)))
      )
      .subscribe();
  }

  initEmptyForm() {
    this.recipeForm = this.formBuilder.group({
      title: new FormControl("", Validators.required),
    });
  }

  initModifyForm(recipebook: Recipe) {
    this.recipeForm = this.formBuilder.group({
      id: recipebook.id,
      title: recipebook.title,
    });
  }
}
