import {
  Component,
  OnInit,
  EventEmitter,
  Output,
  NgModule,
} from "@angular/core";
import { MatDialog } from "@angular/material/dialog";
import {
  FormBuilder,
  FormGroup,
  FormControl,
  Validators,
  FormsModule,
  FormArray,
} from "@angular/forms";
import { ConfirmationDialogComponent } from "src/app/core/confirmation-dialog/confirmation-dialog.component";
import { ActivatedRoute, Router } from "@angular/router";
import { mealService } from "src/app/services/meal.service";
import { Meal } from "src/app/models/meal.model";
import { Observable, catchError, EMPTY } from "rxjs";
import { map, switchMap, tap } from "rxjs/operators";
import { MatRadioModule } from "@angular/material/radio";
import { MatCheckbox } from "@angular/material/checkbox";
import { DietBook } from "src/app/models/diet.model";
import { DietBooksService } from "src/app/services/diet-book.service";
import { MessageService } from "src/app/services/snackBar.service";
import { AutofocusDirective } from "src/app/directive/autofocus.directive";

@Component({
  selector: "app-mealform",
  templateUrl: "./mealform.component.html",
  styleUrls: ["./mealform.component.scss"],
})
export class MealformComponent implements OnInit {
  dietMealForm!: FormGroup;
  collationListControls: FormControl[] = [];
  dietMeal!: Meal;
  errorMsg!: string;
  mode!: string;
  mealId!: string;
  dietBookId!: string;
  loading!: Boolean;
  dietBook!: DietBook;
  dietBook$!: Observable<DietBook>;
  selectedOption!: string;
  checkAll!: boolean;
  selectedStages!: number[];
  textCheckAll!: string;
  stageNb!: number;
  activeButton!: number;
  meals: string[] = ["Petit-déjeuner", "Collation", "Déjeuner", "Diner"];
  dialogRef!: any;

  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private dietBooks: DietBooksService,
    private mealService: mealService,
    private route: ActivatedRoute,
    private dialog: MatDialog,
    private messageService: MessageService
  ) {}
  @Output() cancelClicked: EventEmitter<void> = new EventEmitter<void>();

  ngOnInit() {
    this.loading = true;
    this.checkAll = false;
    this.selectedStages = [];
    this.initEmptyForm();
    this.route.queryParams
      .pipe(
        switchMap((params) => {
          this.mealId = params["mealId"];
          this.dietBookId = params["dietId"];
          this.dietBooks.getDietBookById(this.dietBookId).subscribe((book) => {
            this.stageNb = book.nbStage;
          });
          if (this.mealId === "addMeal") {
            this.mode = "new";
            this.selectedStages = [];
            this.initEmptyForm();

            this.loading = false;
            return EMPTY;
          } else {
            this.mode = "edit";

            this.initEmptyForm();
            return this.mealService.getMealById(params["mealId"]);
          }
        }),
        tap((dietMeal) => {
          if (dietMeal) {
            this.dietMeal = dietMeal;
            this.selectedOption = dietMeal.typeMeal;
            this.selectedStages = dietMeal.stage.map((k) => Number(k));
            this.initModifiyForm(dietMeal);
          }
        }),
        catchError((error) => (this.errorMsg = JSON.stringify(error)))
      )
      .subscribe();
  }

  onClickButtonMeal(name: string) {
    this.selectedOption = name;
    this.dietMealForm.patchValue({
      typeMeal: name,
    });
  }
  getRange(stage: number): number[] {
    return Array(stage)
      .fill(0)
      .map((_, index) => index + 1);
  }

  toggleCheckAll() {
    this.checkAll = !this.checkAll;

    this.textCheckAll = this.checkAll
      ? "Tout désélectionner"
      : "Tout sélectionner";

    // Réinitialiser le tableau
    this.selectedStages = [];

    // Générer un tableau de 0 à stageNb
    const stagesRange = Array.from(Array(this.stageNb).keys());

    // Transformer en tableau de nombres
    const stagesNumbers = stagesRange.map((num) => Number(num + 1));
    console.log(stagesNumbers, this.checkAll);
    // Assigner selon le statut de checkAll
    this.selectedStages = this.checkAll ? stagesNumbers : [];
  }

  onCheckBoxChange(num: number, event: Event) {
    const checkbox = event.target as HTMLInputElement;
    if (checkbox.checked) {
      this.selectedStages.push(num);
    } else {
      const index = this.selectedStages.indexOf(num);
      if (index > -1) {
        this.selectedStages.splice(index, 1);
      }
    }
  }
  showMeal() {
    if (this.selectedOption === "Déjeuner" || this.selectedOption === "Diner") {
      return true;
    } else {
      return false;
    }
  }

  initEmptyForm() {
    this.dietMealForm = this.formBuilder.group({
      typeMeal: new FormControl(""),
      collation: new FormControl(""),
      stage: new FormControl([], Validators.required),
      meat: new FormControl(""),
      meatQuantity: new FormControl("180"),
      meatList: new FormControl(""),
      sauce: new FormControl("1"),
      sideDish: new FormControl(""),
      sideDishQuantity: new FormControl("4-5"),
      sideDishList: new FormControl(""),
      drink: new FormControl(""),
      obs: new FormControl(""),
      dietBookId: new FormControl(`${this.dietBookId}`),
      id: new FormControl("0"),
      collationList: this.formBuilder.array([]),
    });
    this.textCheckAll = "Tout sélectionner";
  }
  initModifiyForm(meal: Meal) {
    this.dietMealForm = this.formBuilder.group({
      id: meal.id,
      typeMeal: meal.typeMeal,
      collation: meal.collation,
      collationList: this.formBuilder.array(meal.collationList),
      stage: [meal.stage],
      meat: meal.meat,
      meatQuantity: meal.meatQuantity,
      meatList: meal.meatList,
      sauce: meal.sauce,
      sideDish: meal.sideDish,
      sideDishQuantity: meal.sideDishQuantity,
      sideDishList: meal.sideDishList,
      drink: meal.drink,
      obs: meal.obs,
    });

    if (this.selectedStages.length === this.stageNb) {
      this.textCheckAll = "Tout déselectionner";
    } else {
      this.textCheckAll = "Tout sélectionner";
    }
    if (meal.collationList && meal.collationList.length > 0) {
      const collationArray = this.dietMealForm.get(
        "collationList"
      ) as FormArray;
      this.collationListControls = collationArray.controls.map(
        (control) => control as FormControl
      );

      meal.collationList.forEach((collation, index) => {
        this.collationListControls[index].setValue(collation);
      });
    }
  }

  onCancel() {
    this.cancelClicked.emit;
    console.log("annulation dans mealForm");
    this.router.navigateByUrl(`dietbook?dietId=${this.dietBookId}`);
  }
  openConfirmationDialog() {
    let message =
      "Êtes-vous sûr de vouloir supprimer ce repas ?<br> cela entraînera la suppression définitive du repas et dans tous les semaines qui lui sont associées.";
    this.dialogRef = this.dialog.open(ConfirmationDialogComponent, {
      width: "500px",
      data: { message: message },
      panelClass: "custom-dialog-container",
    });

    this.dialogRef.afterClosed().subscribe((result: any) => {
      if (result) {
        this.mealService
          .deleteMeal(this.mealId)
          .pipe(
            tap(() => {
              this.loading = false;
              this.messageService.showMessage(
                "Les Modifications sont bien enregistrées"
              );
              this.router.navigateByUrl(`dietbook?dietId=${this.dietBookId}`);
            }),
            catchError((error) => {
              console.error(error);
              this.loading = false;
              this.messageService.showErrorMessage(
                "Une erreur s'est produite lors de la modification."
              );
              this.errorMsg = error.message;
              return EMPTY;
            })
          )
          .subscribe();
      }
    });
  }

  addCollation() {
    const newCollationControl = this.formBuilder.control("");
    this.collationListControls.push(newCollationControl);
    (this.dietMealForm.get("collationList") as FormArray).push(
      newCollationControl
    );
  }

  removeCollation(index: number) {
    this.collationListControls.splice(index, 1);
    (this.dietMealForm.get("collationList") as FormArray).removeAt(index);
  }

  onSubmitForm() {
    this.loading = true;
    if (this.dietMealForm.value.stage !== this.selectedStages) {
      this.dietMealForm.patchValue({ stage: [] });
    }
    this.dietMealForm.patchValue({
      stage: this.selectedStages.sort((a, b) => a - b),
    });
    if (this.dietMealForm.value.typeMeal !== "Collation") {
      this.dietMealForm.patchValue({ collation: "" });
    }
    const collationListValues = this.collationListControls.map(
      (control) => control.value
    );
    console.log(collationListValues);
    const newDietMeal = {
      id: this.dietMealForm.get("id")!.value,
      typeMeal: this.dietMealForm.get("typeMeal")!.value,
      collation: this.dietMealForm.get("collation")!.value,
      collationList: collationListValues,
      stage: this.dietMealForm.get("stage")!.value,
      meat: this.dietMealForm.get("meat")!.value,
      meatList: this.dietMealForm.get("meatList")!.value,
      meatQuantity: this.dietMealForm.get("meatQuantity")!.value,
      sauce: this.dietMealForm.get("sauce")!.value,
      sideDish: this.dietMealForm.get("sideDish")!.value,
      sideDishQuantity: this.dietMealForm.get("sideDishQuantity")!.value,
      sideDishList: this.dietMealForm.get("sideDishList")!.value,
      drink: this.dietMealForm.get("drink")!.value,
      obs: this.dietMealForm.get("obs")!.value,
      dietBookId: this.dietBookId,
    };

    if (this.mode === "new") {
      this.mealService
        .addMeal(newDietMeal)
        .pipe(
          tap(() => {
            this.loading = false;
            this.messageService.showMessage("Le repas a bien été créé");
            this.router.navigateByUrl(`dietbook?dietId=${this.dietBookId}`);
          }),
          catchError((error) => {
            console.error(error);
            this.loading = false;
            this.messageService.showErrorMessage(
              "Une erreur est survenue lors de la création"
            );
            this.errorMsg = error.message;
            return EMPTY;
          })
        )
        .subscribe();
    } else if (this.mode === "edit") {
      this.mealService
        .modifyDietMeal(this.mealId, newDietMeal)
        .pipe(
          tap(() => {
            this.loading = false;
            this.messageService.showMessage("Le repas a bien été modifié");
            this.router.navigateByUrl(`dietbook?dietId=${this.dietBookId}`);
          }),
          catchError((error) => {
            console.error(error);
            this.loading = false;
            this.messageService.showErrorMessage(
              "Une erreur est survenue lors de la modification"
            );
            this.errorMsg = error.message;
            return EMPTY;
          })
        )
        .subscribe();
    }
  }
}
<div *ngIf="mode">
  <form class="container-form" [formGroup]="dietMealForm">
    <!--Section choix des repas-->
    <div class="container">
      <label class="label-col1" for="repas">Repas</label>
      <div class="container-input" formControlName="typeMeal">
        <button
          *ngFor="let meal of meals"
          (click)="onClickButtonMeal(meal)"
          [class.active]="selectedOption == meal"
          name="buttonMeal"
        >
          {{ meal }}
        </button>
      </div>
    </div>

    <!--Section du choix des collation si collation est choisie-->
    <div
      class="container collation"
      [ngClass]="{
        'fade-in': selectedOption === 'Collation',
        hidden: selectedOption !== 'Collation'
      }"
    >
      <label class="label-col1" for="repas">collation </label>
      <mat-radio-group class="mat-radio-button" formControlName="collation">
        <mat-radio-button value="am">AM</mat-radio-button>
        <mat-radio-button value="pm1">PM1</mat-radio-button>
        <mat-radio-button value="pm2">PM2</mat-radio-button>
      </mat-radio-group>
    </div>

    <!--Section Semaine-->
    <div class="container">
      <label class="label-col1" for="stage">Semaine</label>
      <div class="container-input">
        <button class="selectButton" (click)="toggleCheckAll()">
          {{ textCheckAll }}
        </button>
        <span *ngFor="let i of getRange(stageNb)">
          <input
            type="checkbox"
            [name]="'stage' + i"
            [checked]="selectedStages.includes(i)"
            (change)="onCheckBoxChange(i, $event)"
          />
        </span>
      </div>
    </div>

    <!--Section saisie des données-->

    <div *ngIf="!showMeal()" formArrayName="collationList">
      <div
        *ngFor="let collationControl of collationListControls; let i = index"
      >
        <div class="form-group">
          <label for="collation{{ i }}">Collation {{ i + 1 }}</label>
          <input
            autofocus
            type="text"
            [formControlName]="'collationList' + i"
            appAutofocus
          />
          <button type="button" (click)="removeCollation(i)">-</button>
        </div>
      </div>

      <!-- <div
        *ngFor="let collationControl of collationListControls; let i = index; let last = last"
      >
        <div class="form-group">
          <label for="collation{{ i }}">Collation {{ i + 1 }}</label>
          <input
            [appAutofocus]="last"
            type="text"
            [formControl]="collationControl"
            formControlName="collationList"
          />
          <button type="button" (click)="removeCollation(i)">-</button>
        </div>
      </div> -->
      <button type="button" (click)="addCollation()">+</button>
    </div>

    <div *ngIf="showMeal()">
      <div class="container">
        <label class="label-col1" for="meat">Viande</label>
        <div class="container-input">
          <input
            formControlName="meat"
            class="input-col1"
            id="meat"
            type="text"
          />

          <div class="container">
            <label class="label-col2" for="meatQuantity">Quantité Viande</label>
            <input
              class="input-col2 quantity"
              id="meatQuantity"
              type="text"
              formControlName="meatQuantity"
            />
          </div>
          <p>gr.</p>
        </div>
      </div>
      <div class="container">
        <label class="label-col1" for="meatList">Liste des viandes</label>
        <div class="container-input">
          <input
            class="input-col3"
            id="meatList"
            type="text"
            formControlName="meatList"
          />
        </div>
      </div>
      <div class="container">
        <label class="label-col1" for="sauce">Sauce</label>
        <div class="container-input">
          <input
            class="input-col1 quantity"
            id="sauce"
            type="text"
            formControlName="sauce"
          />

          <p>CS</p>
        </div>
      </div>
      <div class="container">
        <label class="label-col1" for="sideDish">Accompagnement</label>
        <div class="container-input">
          <input
            class="input-col1"
            id="sideDish"
            type="text"
            formControlName="sideDish"
          />

          <label class="label-col2" for="sideDishQuantity">Quantité Acc.</label>

          <input
            class="input-col2 quantity"
            id="sideDishQuantity"
            type="text"
            formControlName="sideDishQuantity"
          />

          <p>CS</p>
        </div>
      </div>
      <div class="container">
        <label class="label-col1" for="sideDishList">Liste des Acc.</label>
        <div class="container-input">
          <input
            class="input-col3"
            id="sideDishList"
            type="text"
            formControlName="sideDishList"
          />
        </div>
      </div>
      <div class="container">
        <label class="label-col1" for="drink">Boissons</label>
        <div class="container-input">
          <input
            class="input-col3"
            id="drink"
            type="text"
            formControlName="drink"
          />
        </div>
      </div>
      <div class="container observation">
        <label class="label-col1 observation" for="obs">Observation</label>
        <div class="container-input">
          <textarea
            class="input-col3"
            id="obs"
            type="text"
            formControlName="obs"
          ></textarea>
        </div>
      </div>
    </div>

    <div class="container-buttons">
      <div class="action-buttons">
        <button type="submit" (click)="onCancel()">Retour</button>
      </div>
      <div class="action-buttons">
        <button mat-raised-button (click)="openConfirmationDialog()">
          Supprimer
        </button>
      </div>
      <div class="action-buttons">
        <button type="submit" (click)="onSubmitForm()">Enregistrer</button>
      </div>
    </div>
  </form>
</div>
