import { Component, Inject } from "@angular/core";
import { MAT_DIALOG_DATA, MatDialogRef } from "@angular/material/dialog";
import {
  ConfirmationDialogData,
  StageToRemove,
} from "src/app/models/dialog-data.model";
@Component({
  selector: "app-confirmation-dialog",
  templateUrl: "./confirmation-dialog.component.html",
  styleUrls: ["./confirmation-dialog.component.scss"],
})
export class ConfirmationDialogComponent {
  constructor(
    public dialogRef: MatDialogRef<ConfirmationDialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: ConfirmationDialogData
  ) {}

  onNoClick(): void {
    this.dialogRef.close();
  }
}
<h1 mat-dialog-title>Confirmation</h1>
<div mat-dialog-content>
  <p [innerHTML]="data.message"></p>
  <ng-container *ngIf="data.stagesToRemove && data.stagesToRemove.length > 0">
    <p>Sélectionnez les étapes à supprimer :</p>
    <div class="container-checkbox">
      <div *ngFor="let stage of data.stagesToRemove">
        <label>
          <input type="checkbox" [(ngModel)]="stage.selected" />
          {{ stage.value }}
        </label>
      </div>
    </div>
  </ng-container>
</div>
<div mat-dialog-actions class="action-buttons">
  <button mat-button (click)="onNoClick()">Annuler</button>
  <button mat-button [mat-dialog-close]="true" cdkFocusInitial>Valider</button>
</div>
